	render_cube();	
}

draw_lights(in shader shader, in vec3[] positions, in vec3[] colors)
{
	for (var i = 0; i < 4; ++i)
	{
		var mt = translate(mat4(), positions[i]);
		mt = scale(mt, vec3(0.25f, 0.25f, 0.25f));
		set_uniform(shader, "model", mt);
		set_uniform(shader, "lightColor", colors[i]);
		render_cube();
	}
}

