node_val.frag:
#version 110

varying vec2 TexCoord;

uniform sampler2D main_tex;

uniform float blur_radius;

#define KernelSampleCount 22

uniform vec2 kernels[22];

void main()
{
	const vec2 texel_size = vec2(1.0 / 512.0, 1.0/ 512.0);

	vec3 color = vec3(0.0);
	for (int k = 0; k < KernelSampleCount; k++) 
	{
		vec2 o = kernels[k] * blur_radius;
		o *= texel_size;
		vec4 s = texture2D(main_tex, TexCoord + o);
		color += s.rgb;
	}
	color *= 1.0 / 22.0;
    gl_FragColor = vec4(color, 1.0);
}

