#version 110

varying vec2 TexCoord;

uniform sampler2D base_tex;
uniform sampler2D bloom_tex;
uniform float bright;

void main()
{
    vec3 base_col = texture2D(base_tex, TexCoord).rgb;      
    vec3 bloom_col = texture2D(bloom_tex, TexCoord).rgb;
    vec3 result = base_col + bloom_col * bright;
    gl_FragColor = vec4(result, 1.0);
}
